// Displays name, artist and picture
import React from "react";

const LibrarySong = ({ song, songs, setCurrentSong, id, key, audioRef }) => {
  const songSelect = () => {
    setCurrentSong(song);
    // Song not loaded up -> use Promise() -> when fully loaded -> 
    audioRef.current.play();
  };
  return (
    <div onClick={songSelect} className="library-song">
      <img src={song.cover} alt={song.name}></img>
      <div className="song-description">
        <h3> {song.name} </h3>
        <h4> {song.artist} </h4>
      </div>
    </div>
  );
};

export default LibrarySong;
